// تعریف آرایه‌ها
var standPose = [
    123, 170, 165, 45, 
    100, 132, 70, 85, 
    205, 140, 170, 195, 
    130, 130, 190, 100
];

var stepPose = [
    123, 190, 165, 45, 
    100, 132, 115, 55, 
    205, 140, 254, 170, 
    179, 225, 200, 100
];

// دریافت وضعیت قبلی از حافظه (اگر بار اوله، فرض می‌کنیم آخرین بار Step بوده تا الان Stand بفرسته)
var lastState = context.get('lastPose') || 'step';

// متغیر برای ذخیره دستور نهایی
var command = "";

if (lastState === 'step') {
    // --- نوبت ارسال STAND است ---
    command = "M:" + standPose.join(",") + "\n";

    // ذخیره وضعیت جدید برای دفعه بعد
    context.set('lastPose', 'stand');

    // نمایش وضعیت زیر نود (برای قشنگی)
    node.status({fill:"blue", shape:"dot", text:"Sent: STAND"});
} 
else {
    // --- نوبت ارسال STEP است ---
    command = "M:" + stepPose.join(",") + "\n";

    // ذخیره وضعیت جدید برای دفعه بعد
    context.set('lastPose', 'step');

    // نمایش وضعیت زیر نود
    node.status({fill:"green", shape:"dot", text:"Sent: STEP"});
}

msg.payload = command;
return msg;
